function processWorkNotes(current) {
	var grWN = new GlideRecord('sys_journal_field');
	grWN.addEncodedQuery("element=work_notes" + 
						 "^name=" + current.getTableName() +
						 "^element_id=" + current.sys_id);
	grWN.query();
	while (grWN.next()) {
		gs.info(grWN.value);
	}
}
function getJiraComments(current) {
	var ji = new x_303301_jira_inte.JiraIntegration();
	try {
		var oData = ji._callREST('rest/api/2/issue/' + current.correlation_id + '/comment', 'get', '','');
		for (var i=0; i < oData.body.comments.length; i++) {
			ji.debug(oData.body.comments[i].body.toString());
		}
		//ji.debug(JSON.stringify(oData));
	} catch(ex) {
		ji.debug('ERROR in getJiraComments ' + ex);
	} finally {
		ji.sendToLog();
	}
}
function addCommentToJira(sKey) {
	var ji = new x_303301_jira_inte.JiraIntegration();
	try {
		var oComment = {};
		oComment.body = "this is a test comment from ServiceNow";
		var oData = ji._callREST('rest/api/2/issue/' + sKey + '/comment', 'post', JSON.stringify(oComment),'');
		ji.debug(JSON.stringify(oData));
	} catch(ex) {
		ji.debug('ERROR in getJiraComments ' + ex);
	} finally {
		ji.sendToLog();
	}
}
